import {
  CREATE_OPCEN,
  //   DELETE_OPCEN,
  //   UPDATE_OPCEN,
  //   ADD_ADMIN,
  //   DELETE_ADMIN,
} from "./types";

export const setCreateOpCen = opcenType => dispatch => {
  dispatch({
    type: CREATE_OPCEN,
    payload: { opcenType },
  });
};

export const registerOpcen = ({
  _id,
  name,
  category,
  number,
  description,
}) => async dispatch => {
  const config = {
    headers: {
      "Content-Type": "application/json",
    },
  };
  const body = JSON.stringify({ _id, name, category, number, description });
  try {
    const res = await axios.post("/api/opcen", body, config);
    dispatch({
      type: REGISTER_SUCCESS,
      payload: res.data,
    });
  } catch (err) {
    const errors = err.response.data.errors;
    if (errors) {
      errors.forEach(error => dispatch(setAlert(error.msg, "danger")));
    }

    dispatch({
      type: REGISTER_FAIL,
    });
  }
};
