import React, { Fragment } from "react";
import PropTypes from "prop-types";
import { Link } from "react-router-dom";
import Moment from "react-moment";
import { connect } from "react-redux";
import { addLike, removeLike, deletePost } from "../../actions/post";
import Spinner from "../layout/Spinner";

const MAX_LENGTH = 150;

const PostItem = ({
  addLike,
  removeLike,
  deletePost,
  auth,
  profile: { profiles, loading },
  post: { _id, text, name, lname, articleImage, user, likes, comments, date },
  showActions,
}) => {
  return (
    <Fragment>
      {loading ? (
        <Spinner />
      ) : (
        <div className='post-large bg-white'>
          {/* Image post container */}
          <div
            style={{
              background: "#eee",
              margin: "auto",
              maxWidth: "100%",
            }}
          >
            <Link to={`/posts/${_id}/${articleImage}`}>
              <img
                // style={{ width: "305px", borderRadius: "5px" }}
                className='postImage'
                src={`/img/${articleImage}`}
                alt='...'
              />
            </Link>

            {/* user details */}

            <Link to={`/profile/${user}`}>
              {/* map all profiles here */}
              {profiles.length > 0 ? (
                profiles.map(profile => {
                  if (profile.user._id === user) {
                    // <PostHeader key={profile._id} profile={profile} />;
                    // console.log(" Profile", profile._id);
                    return (
                      <Fragment>
                        <div
                          style={{
                            display: "flex",
                            flexDirection: "row",
                            width: "100%",
                            margin: "0px 1px",
                            alignItems: "center",
                            padding: "2%",
                            justifyContent: "space-around",
                            borderBottom: "1px solid #ddd",
                          }}
                        >
                          <div className=' p'>
                            <img
                              className='post-profile'
                              src={`/img/${profile.profilepic}`}
                              alt='...'
                            />{" "}
                          </div>
                          <div
                            style={{
                              width: "65%",
                              marginLeft: "20px",
                            }}
                          >
                            <div
                              style={{
                                display: "flex",
                                flexDirection: "row",
                              }}
                            >
                              <h4 className='p-11'>{name}</h4>{" "}
                              <h4 className='p-11'>{lname}</h4>{" "}
                            </div>

                            <p className='post-date p-11 '>
                              {" "}
                              Posted <Moment format='LLLL'>{date}</Moment>
                            </p>
                          </div>
                          <div style={{ width: "20%" }}></div>
                          <div style={{ width: "5%" }}>
                            <i
                              className='fa fa-ellipsis-h'
                              aria-hidden='true'
                            ></i>
                          </div>
                        </div>
                      </Fragment>
                    );
                  }
                })
              ) : (
                <div>
                  <img
                    className='post-profile'
                    src={`/img/Spotter.png`}
                    alt='...'
                  />
                </div>
              )}
            </Link>
          </div>
          <div>
            {/* message post  */}
            <div>
              {text.length > MAX_LENGTH ? (
                <div className='m-1'>
                  {`${text.substring(0, MAX_LENGTH)}...`}{" "}
                  <Link to={`/posts/${_id}/${articleImage}`}>see more</Link>
                </div>
              ) : (
                <p className='m-1'>{text}</p>
              )}
            </div>

            {/* Action Buttons */}
            <div
              style={{
                display: "flex",
                flexDirection: "row",
                height: "70px",
                // margin: "0px 5px",
                alignItems: "center",
                justifyContent: "space-around",
              }}
            ></div>
          </div>
        </div>
      )}
    </Fragment>
  );
};
//console.log(PostItem);
// set show action to true so buttons would not show in single post
PostItem.defaultProps = {
  showActions: true,
};

PostItem.propTypes = {
  post: PropTypes.object.isRequired,
  profile: PropTypes.object.isRequired,
  auth: PropTypes.object.isRequired,
  addLike: PropTypes.func.isRequired,
  removeLike: PropTypes.func.isRequired,
  deletePost: PropTypes.func.isRequired,
};

const mapStateToProps = state => ({
  auth: state.auth,
  profile: state.profile,
});

export default connect(mapStateToProps, {
  addLike,
  removeLike,
  deletePost,
})(PostItem);
