import React, { Fragment, useState, useEffect } from "react";
import { Link, Redirect } from "react-router-dom";
import { connect } from "react-redux";
import { setAlert } from "../../actions/alert";
import { registerOpcen } from "../../actions/opcen";
import PropTypes from "prop-types";

const Dashboard_opcen = props => {
  const [formData, setFormData] = useState({
    name: "",
    category: "",
    description: "",
  });

  //destructure so you would do formData.name formData.number
  //Object Syntax use {}
  const { name, category, description } = formData;

  const onChange = async c =>
    setFormData({ ...formData, [c.target.name]: c.target.value });

  const onSubmit = async c => {
    c.preventDefault();
    registerOpcen({ name, category, description });
  };
  //   if (isAuthenticated) {
  //     return <Redirect to={`/operation-center/${user}`} />;
  //   }

  return (
    <Fragment>
      <div
        style={{
          padding: "20px",
          background: "#fff",
          borderRadius: "10px",
        }}
      >
        <div
          style={{
            display: "block",
            borderRadius: "10px",

            padding: "20px",
          }}
        >
          <h1 className='large text-primary'> Operation Center Console</h1>
        </div>

        <div
          style={{
            display: "flex",
            flexDirection: "row",
            width: "100%",
            justifyContent: "space-around",
          }}
        >
          <Link className='smallIcon'>
            <div style={{ marginTop: "22%" }}>
              <i className='fas fa-landmark' aria-hidden='true'></i>
              <p>Profile</p>
              <div
                style={{ fontSize: "10px", lineHeight: "10px", color: "#333" }}
              >
                (update your opcen profile)
              </div>
            </div>
          </Link>
          <Link className='smallIcon'>
            <div style={{ marginTop: "22%" }}>
              <i className='fas fa-landmark' aria-hidden='true'></i>
              <p>Responders</p>
              <div
                style={{ fontSize: "10px", lineHeight: "10px", color: "#333" }}
              >
                (manage you responders)
              </div>
            </div>
          </Link>
          <Link className='smallIcon'>
            <div style={{ marginTop: "22%" }}>
              <i className='fa fa-hand-paper-o' aria-hidden='true'></i>
              <p>Incidents</p>
              <small className='small-txt-blk'>(incident dashboard)</small>
            </div>
          </Link>
          <Link className='smallIcon'>
            <div style={{ marginTop: "22%" }}>
              <i className='fa fa-building-o' aria-hidden='true'></i>
              <i aria-hidden='true'></i>
              <p>Notification</p>
              <small className='small-txt-blk'>
                (update your team and constituents)
              </small>
            </div>
          </Link>
        </div>
        {/* Another row start */}

        <div
          style={{
            display: "flex",
            flexDirection: "row",
            width: "100%",
            justifyContent: "space-around",
          }}
        >
          <Link className='bigIcon'>
            <div style={{ marginTop: "30%" }}>
              <i className='fas fa-landmark' aria-hidden='true'></i>
              <p>Live Map</p>
              <small className='small-txt-blk'>
                (current location of your responders)
              </small>
            </div>
          </Link>
          <Link className='bigIcon'>
            <div style={{ marginTop: "30%" }}>
              <i className='fa fa-hand-paper-o' aria-hidden='true'></i>
              <p>Facilities</p>
              <small className='small-txt-blk'>(incident dashboard)</small>
            </div>
          </Link>
          <Link className='bigIcon'>
            <div style={{ marginTop: "25%" }}>
              <i className='fa fa-building-o' aria-hidden='true'></i>
              <i aria-hidden='true'></i>
              <p>Notification</p>
              <div
                style={{ fontSize: "10px", lineHeight: "10px", color: "#333" }}
              >
                (update your team and constituents)
              </div>
            </div>
          </Link>
        </div>
        {/* Another Row */}

        <div
          style={{
            display: "flex",
            flexDirection: "row",
            width: "100%",
            justifyContent: "space-around",
          }}
        >
          <Link className='smallIcon'>
            <div style={{ marginTop: "22%" }}>
              <i className='fas fa-landmark' aria-hidden='true'></i>
              <p>Profile</p>
              <div
                style={{ fontSize: "10px", lineHeight: "10px", color: "#333" }}
              >
                (update your opcen profile)
              </div>
            </div>
          </Link>
          <Link className='smallIcon'>
            <div style={{ marginTop: "22%" }}>
              <i className='fas fa-landmark' aria-hidden='true'></i>
              <p>Responders</p>
              <div
                style={{ fontSize: "10px", lineHeight: "10px", color: "#333" }}
              >
                (manage you responders)
              </div>
            </div>
          </Link>
          <Link className='smallIcon'>
            <div style={{ marginTop: "22%" }}>
              <i className='fa fa-hand-paper-o' aria-hidden='true'></i>
              <p>Incidents</p>
              <div
                style={{ fontSize: "10px", lineHeight: "10px", color: "#333" }}
                className='small-txt-blk'
              >
                (incident dashboard)
              </div>
            </div>
          </Link>
          <Link className='smallIcon'>
            <div style={{ marginTop: "22%" }}>
              <i className='fa fa-building-o' aria-hidden='true'></i>
              <i aria-hidden='true'></i>
              <p>Notification</p>
              <div
                style={{ fontSize: "10px", lineHeight: "10px", color: "#333" }}
                className='small-txt-blk'
              >
                (update your team and constituents)
              </div>
            </div>
          </Link>
        </div>
      </div>
    </Fragment>
  );
};

Dashboard_opcen.propTypes = {
  props: PropTypes,
};

const mapStateToProps = state => ({});

const mapDispatchToProps = {};

export default connect(mapStateToProps, mapDispatchToProps)(Dashboard_opcen);
