import {
  CREATE_INCIDENT,
  ERROR_CREATE_INCIDENT,
  CLEAR_INCIDENT,
  INCIDENT_SUMMIT_SUCCESS,
  GET_INCIDENTS_BY_USER,
  GET_INCIDENT_BY_OPCEN,
  GET_ALL_INCIDENT,
  GET_INCIDENT_BY_ID,
} from "../actions/types";
const initialState = {
  createIncident: false,
  type: null,
  incident: null,
  incidents: null,
  loading: true,
  error: {},
};

export default function (state = initialState, action) {
  const { type, payload } = action;

  switch (type) {
    case CREATE_INCIDENT:
      return {
        ...state,
        createIncident: true,
        type: payload, //from the action file
        loading: false,
      };
    case INCIDENT_SUMMIT_SUCCESS:
      return {
        ...state,
        createIncident: true,
        incident: payload, //from the action file
        loading: false,
      };
    case CLEAR_INCIDENT:
      return {
        ...state,
        createIncident: false,
        type: null,
        incident: null,
        incidents: null,
        loading: false,
      };

    default:
      return state;
  }
}
