import React, { Fragment, useEffect } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import Spinner from "../layout/Spinner";
import { getimagePost } from "../../actions/post";
import PostItem from "../posts/PostItem";

const ImagePost = ({
  getimagePost: { _id, text, name, artilcleImage, loading },

  match,
}) => {
  useEffect(() => {
    getimagePost(match.params.id);
  }, [getimagePost, match.params.id]);
  return (
    <Fragment>
      <div className='posts'>
        <img className='post-profile' src={`/img/${artilcleImage}`} alt='...' />
      </div>
    </Fragment>
  );
};

ImagePost.propTypes = {
  getimagePost: PropTypes.func.isRequired,
  post: PropTypes.object.isRequired,
};

const mapStateToProps = state => ({
  post: state.post,
});

export default connect(mapStateToProps, { getimagePost })(ImagePost);
