import React, { Fragment, useState } from "react";
import { Link, withRouter } from "react-router-dom";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { addEmergencyInfo } from "../../actions/profile";

const AddEmergencyInfo = ({ addEmergencyInfo, history }) => {
  const [formData, setFormData] = useState({
    contactperson: "",
    relationship: "",
    contactnumber: "",
    address: "",
    bloodtype: "",
    build: "",
    birthmark: "",
    height: "",
    weight: "",
    eyecolor: "",
    haircolor: "",
    insured: "false",
  });

  const {
    contactperson,
    relationship,
    contactnumber,
    address,
    bloodtype,
    build,
    birthmark,
    height,
    weight,
    eyecolor,
    haircolor,
    insured,
  } = formData;

  const onChange = async c =>
    setFormData({ ...formData, [c.target.name]: c.target.value });

  const onSubmit = async c => {
    c.preventDefault();
    addEmergencyInfo(formData, history);
  };

  return (
    <Fragment>
      <h1 className='large text-primary'>Add Your Education</h1>
      <p className='lead'>
        <i className='fas fa-graduation-cap'></i> Add any school, bootcamp, etc
        that you have attended
      </p>
      <small>* = required field</small>
      <form className='form' onSubmit={c => onSubmit(c)}>
        <div className='form-group'>
          <input
            type='text'
            placeholder='* Contact Person'
            name='contactperson'
            value={contactperson}
            onChange={c => onChange(c)}
            required
          />
        </div>

        <div className='form-group'>
          <input
            type='text'
            placeholder='* Relationship'
            name='relationship'
            value={relationship}
            onChange={c => onChange(c)}
            required
          />
        </div>
        <div className='form-group'>
          <input
            type='tel'
            placeholder='09XX XXX XXXX'
            name='number'
            value={number}
            onChange={c => onChange(c)}
            required
          />
        </div>
        <div className='form-group'>
          <input
            type='text'
            placeholder='Address'
            name='address'
            value={address}
            onChange={c => onChange(c)}
          />
        </div>

        {/* <div className='form-group'>
          <p>
            <input
              type='checkbox'
              name='current'
              value={current}
              onChange={c => {
                setFormData({ ...formData, current: !current });
                toggleDisabled(!toDateDisabled);
              }}
            />{" "}
            Current School or Bootcamp
          </p>
        </div> */}

        <div className='form-group'>
          <textarea
            name='description'
            cols='30'
            rows='5'
            placeholder='Program Description'
            value={description}
            onChange={c => onChange(c)}
          ></textarea>
        </div>
        <input type='submit' className='btn btn-primary my-1' />
        <Link className='btn btn-light my-1' to='/dashboard'>
          Go Back
        </Link>
      </form>
    </Fragment>
  );
};

AddEmergencyInfo.propTypes = {
  addEmergencyInfo: PropTypes.func.isRequired,
};

export default connect(null, { addEducation })(withRouter(AddEmergencyInfo));
