import React, { Fragment, useEffect } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import Spinner from "../layout/Spinner";
import { getOpcens } from "../../actions/opcen";
import { loadUser } from "../../actions/auth";
import OpcenItem from "./OpcenItem";

const Opcens = ({ getOpcens, loadUser, user, opcen: { opcens, loading } }) => {
  useEffect(() => {
    loadUser();
    getOpcens(user);
  }, [getOpcens]);
  return loading ? (
    <Spinner />
  ) : (
    <Fragment>
      <p className='lead'>
        <i className='fas fa-user'></i> Choose an Operation Center
      </p>

      <div
        style={{
          display: "flex",
          flexDirection: "row",
          width: "100%",
          height: "100%",
          justifyContent: "space-around",
        }}
      >
        {opcens.map(opcen => (
          <div className='posts'>
            <OpcenItem key={opcen._id} opcen={opcen} />
          </div>
        ))}
      </div>
    </Fragment>
  );
};

Opcens.propTypes = {
  getOpcens: PropTypes.func.isRequired,
  loadUser: PropTypes.func.isRequired,
  opcen: PropTypes.func.isRequired,
};

const mapStateToProps = state => ({
  opcen: state.opcen,
  user: state.auth.user._id,
});

export default connect(mapStateToProps, { getOpcens, loadUser })(Opcens);
