import React, { Fragment, useEffect } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import Dashboard from "../operation-center/Dashboard_opcen";
import Spinner from "../layout/Spinner";
import { getOpcenProfileById } from "../../actions/opcenprofile";

const OpcenConsole = ({
  getOpcenProfileById,
  opcen_id,
  opcen: { opcen, loading },
}) => {
  useEffect(() => {
    getOpcenProfileById(opcen_id);
  }, []);

  return (
    <Fragment>
      {loading || opcen === null ? (
        <Spinner />
      ) : (
        <div
          style={{
            display: "flex",
            flexDirection: "row",
            justifyContent: "flex-start",
            alignItems: "center",
            background: "#fff",
            borderRadius: "10px",
            marginBottom: "5px",
            height: "100px",
          }}
        >
          <div>
            <img
              style={{
                height: "88px",
                width: "88px",
                padding: "8px",
              }}
              src={`/img/Spotter.png`}
              alt=''
            />
          </div>
          <div>
            <h2> {opcen.name} Console</h2>
            <p> {opcen.category}</p>
          </div>
        </div>
      )}
      <Dashboard />
    </Fragment>
  );
};

OpcenConsole.propTypes = {
  opcen: PropTypes.object.isRequired,
  opcen_id: PropTypes.object.isRequired,
};

const mapStateToProps = state => ({
  opcen: state.opcen,
  opcen_id: state.opcen.opcen._id,
});

export default connect(mapStateToProps, null)(OpcenConsole);
