function urlBase64ToUint8Array(base64String) {
  const padding = "=".repeat((4 - (base64String.length % 4)) % 4);
  const base64 = (base64String + padding)
    .replace(/\-/g, "+") /* eslint-disable-line */
    .replace(/_/g, "/"); /* eslint-disable-line */

  const rawData = window.atob(base64);
  const outputArray = new Uint8Array(rawData.length);

  for (let i = 0; i < rawData.length; ++i) {
    outputArray[i] = rawData.charCodeAt(i);
  }
  return outputArray;
}

export async function send(
  title = "GUARDIAN NOTIFICATION",
  body = "Incident Alert"
) {
  console.log("Hit by Frontend");
  const register = await navigator.serviceWorker.register("/ws.js", {
    scope: "/",
  });

  if ("serviceWorker" in navigator) {
    navigator.serviceWorker.ready
      .then(function (registration) {
        if (!registration.pushManager) {
          return;
        }
        console.log("Service worker Registering...");
        registration.pushManager
          .getSubscription()
          .then(function (existedSubscription) {
            if (existedSubscription === null) {
              registration.pushManager
                .subscribe({
                  applicationServerKey: urlBase64ToUint8Array(
                    process.env.REACT_APP_PUBLIC_VAPID_KEY
                  ),
                  userVisibleOnly: true,
                })
                .then(function (newSubscription) {
                  sendSubscription(newSubscription, title, body);
                })
                .catch(function (e) {
                  if (Notification.permission !== "granted") {
                  } else {
                    console.error(e);
                  }
                });
            } else {
              sendSubscription(existedSubscription, title, body);
            }
          });
      })
      .catch(function (e) {
        console.error(e);
      });
  }
}

function sendSubscription(subscription, title, body) {
  return fetch("/subscribe", {
    method: "POST",
    body: JSON.stringify({ subscription, title, body }),
    headers: {
      "content-type": "application/json",
    },
  });
}
