import React, { Fragment } from "react";
import PropTypes from "prop-types";
import { Link } from "react-router-dom";
import Moment from "react-moment";
import { connect } from "react-redux";
import { addLike, removeLike, deletePost } from "../../actions/post";
import Spinner from "../layout/Spinner";
import CommentItem from "../../components/post/CommentItem";
import CommentForm from "../../components/post/CommentForm";

const MAX_LENGTH = 150;

const PostItem = ({
  addLike,
  removeLike,
  deletePost,
  auth,
  profile: { profiles, loading },
  post: { _id, text, name, lname, articleImage, user, likes, comments, date },
  showActions,
}) => {
  return (
    <Fragment>
      {loading ? (
        <Spinner />
      ) : (
        <div className='post-large bg-white'>
          {/* Image post container */}
          <div
            style={{
              position: "relative",
              height: "500px",
              background: "#eee",
              marginTop: "4rem",
              width: "100%",
              borderRadius: "5px",
            }}
          >
            <Link to={`/posts/${_id}/${articleImage}`}>
              <img
                className='postImageLarge'
                // style={{ width: "305px", borderRadius: "5px" }}
                src={`/img/${articleImage}`}
                alt='...'
              />
            </Link>
          </div>
          <div>
            {/* user details */}

            <Link to={`/profile/${user}`}>
              {/* map all profiles here */}
              {profiles.length > 0 ? (
                profiles.map(profile => {
                  if (profile.user._id === user) {
                    // <PostHeader key={profile._id} profile={profile} />;
                    // console.log(" Profile", profile._id);
                    return (
                      <Fragment>
                        <div
                          style={{
                            display: "flex",
                            flexDirection: "row",
                            width: "100%",

                            alignItems: "center",
                            padding: "2%",
                            backgroundColor: "#fff",
                            justifyContent: "space-around",
                            borderBottom: "1px solid #ddd",
                            marginTop: "4rem",
                          }}
                        >
                          <div className=' p'>
                            <img
                              className='post-profile'
                              src={`/img/${profile.profilepic}`}
                              alt='...'
                            />{" "}
                          </div>
                          <div
                            style={{
                              width: "65%",
                              marginLeft: "20px",
                            }}
                          >
                            <div
                              style={{
                                display: "flex",
                                flexDirection: "row",
                              }}
                            >
                              <h4 className='p-11'>{name}</h4>{" "}
                              <h4 className='p-11'>{lname}</h4>{" "}
                            </div>

                            <p style={{ color: "#aaa", fontSize: "10px" }}>
                              {" "}
                              Posted <Moment format='LLLL'>{date}</Moment>
                            </p>
                          </div>
                          <div style={{ width: "20%" }}></div>
                          <div style={{ width: "5%" }}>
                            <i
                              className='fa fa-ellipsis-h'
                              aria-hidden='true'
                            ></i>
                          </div>
                        </div>
                        <div
                          style={{
                            display: "flex",
                            flexDirection: "row",
                            height: "35px",
                            margin: "5px",
                            alignItems: "center",
                            padding: "10px",
                            justifyContent: "space-around",
                          }}
                        >
                          {showActions && (
                            <Fragment>
                              {/* Button for like */}

                              <Link
                                onClick={c => addLike(_id)}
                                type='button'
                                className='btn-post '
                              >
                                <i
                                  className='fa fa-thumbs-o-up'
                                  aria-hidden='true'
                                ></i>

                                <span className='p'>
                                  {likes.length > 0 && (
                                    <span>{likes.length}</span>
                                  )}
                                </span>
                              </Link>

                              {/* Button for Unlike */}
                              <Link
                                onClick={c => removeLike(_id)}
                                type='button'
                                className='btn-post'
                              >
                                <i
                                  className='fa fa-thumbs-o-down'
                                  aria-hidden='true'
                                ></i>
                              </Link>
                              <Link to={`/posts/${_id}`} className='btn-post'>
                                <i
                                  className='fa fa-comment-o'
                                  aria-hidden='true'
                                ></i>{" "}
                                {comments.length > 0 && (
                                  <span className='comment-count'>
                                    {comments.length}
                                  </span>
                                )}
                              </Link>

                              <Link to={`/posts/${_id}`} className='btn-post'>
                                <i
                                  className='fa fa-share-square-o'
                                  aria-hidden='true'
                                ></i>{" "}
                              </Link>

                              {!auth.loading && user === auth.user._id && (
                                <button
                                  onClick={c => deletePost(_id)}
                                  type='button'
                                  className='btn btn-danger'
                                >
                                  <i className='fas fa-times'></i>
                                </button>
                              )}
                            </Fragment>
                          )}
                        </div>
                      </Fragment>
                    );
                  }
                })
              ) : (
                <div>
                  <img
                    className='post-profile'
                    src={`/img/Spotter.png`}
                    alt='...'
                  />
                </div>
              )}
            </Link>
            {/* message post  */}
            <div
              style={{
                maxHeight: "300px",

                margin: "10px",
                overflow: "auto",
              }}
            >
              <p style={{ fontSize: "13px" }}>{text}</p>
            </div>
          </div>
        </div>
      )}
    </Fragment>
  );
};
//console.log(PostItem);
// set show action to true so buttons would not show in single post
PostItem.defaultProps = {
  showActions: true,
};

PostItem.propTypes = {
  post: PropTypes.object.isRequired,
  profile: PropTypes.object.isRequired,
  auth: PropTypes.object.isRequired,
  addLike: PropTypes.func.isRequired,
  removeLike: PropTypes.func.isRequired,
  deletePost: PropTypes.func.isRequired,
};

const mapStateToProps = state => ({
  auth: state.auth,
  profile: state.profile,
});

export default connect(mapStateToProps, {
  addLike,
  removeLike,
  deletePost,
})(PostItem);
