import React, { useState, useCallback } from "react";
import {
  GoogleMap,
  LoadScript,
  Marker,
  InfoWindow,
  Autocomplete,
} from "@react-google-maps/api";
import Geodcode from "react-geocode";
import AutoComplete from "react-google-autocomplete";

Geodcode.setApiKey("AIzaSyAJjCD4TVrZk6u-OxFvk00OYpBqSMB_RVo");

const Map = () => {
  const [map, setMap] = useState({
    adddress: "",
    city: "",
    area: "",
    state: "",
    zoom: 15,
    height: 400,
    mapPosition: {
      // Load to specific address/location
      lat: 10.3272994,
      lng: 123.9431079,
      // lat: 0,
      // lng: 0,
    },
    markerPosition: {
      // Load to specific address/location
      // lat: 10.3272994,
      // lng: 123.9431079,
      lat: 0,
      lng: 0,
    },
  });

  const onLoad = useCallback(function callback(map) {
    const bounds = new window.google.maps.LatLngBounds();
    map.fitBounds(bounds);
    setMap(map);
  }, []);

  onload(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(position => {
        setMap(
          {
            mapPosition: {
              lat: position.coords.latitude,
              lng: position.coords.longitude,
            },
            markerPosition: {
              lat: position.coords.latitude,
              lng: position.coords.longitude,
            },
          },
          () => {
            Geodcode.fromLatLng(
              position.coords.latitude,
              position.coords.longitude
            ).then(response => {
              console.log("Response", response);

              const address = response.results[0].formatted_address,
                addressArray = response.results[0].address_components,
                city = this.getCity(addressArray),
                state = this.getState(addressArray),
                area = this.getArea(addressArray);

              console.log("city is", city);
              console.log("Area is", area);
              console.log("state is", state);
              console.log("Address is", address);

              this.setState({
                address: address ? address : "",
                city: city ? city : "",
                area: area ? area : "",
                state: state ? city : "",
              });
            });
          }
        );
      });
    }
  }, []);

  const containerStyle = {
    width: "50%",
    height: "200px",
    padding: ".5rem",
    marginTop: ".5rem",
    maxWidth: 1500,
    margin: "auto",
    borderRadius: "5px",
  };

  return (
    <LoadScript googleMapsApiKey='AIzaSyAJjCD4TVrZk6u-OxFvk00OYpBqSMB_RVo'>
      <GoogleMap
        mapContainerStyle={containerStyle}
        center={setMap.mapPosition}
        zoom={map.zoom}

        // defaultOptions={{ styles: mapStyles }}
      >
        {/* <AutoComplete
          className='autocomplete'
          types={["(regions)"]}
           onPlaceSelected={onPlaceSelected}
        /> */}

        {/* <Marker position={map.mapPosition}></Marker> */}
      </GoogleMap>
    </LoadScript>
  );
};

export default Map;
