import React, { Fragment, useEffect, useState } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { getIncidents } from "../../actions/incident";
import { getOpcenProfileById } from "../../actions/opcenprofile";
import Clock from "react-live-clock";
import Moment from "react-moment";
import OpcenConsole from "../opcens/OpcenConsole";

import Spinner from "../layout/Spinner";

const Incidents = ({
  user,
  getOpcenProfileById,
  opcen_id,
  opcen,
  opcen_profile: { profile, loading },
  getIncidents,
  incidents,
}) => {
  useEffect(() => {
    getIncidents();
    getOpcenProfileById();
  }, []);

  var date = new Date();

  return loading ? (
    <Spinner />
  ) : (
    <Fragment>
      <div className='full'>
        iv style=
        {{
          display: "flex",
          flexDirection: "row",
          justifyContent: "flex-start",
          alignItems: "center",
          background: "#fff",
          borderRadius: "10px",
          marginBottom: "5px",
          height: "100px",
        }}
        <div>
          {profile === null ? (
            <img
              style={{
                height: "88px",
                width: "88px",
                padding: "8px",
                marginLeft: "5%",
              }}
              src={`/opcenlogo/guardian.png`}
              alt=''
            />
          ) : (
            <img
              style={{
                height: "88px",
                width: "88px",
                padding: "8px",
                marginLeft: "5%",
              }}
              src={`/opcenlogo/${profile.logo}`}
              alt=''
            />
          )}
        </div>
        <div>
          <h2 className='large text-primary'> {opcen.name}</h2>
          <small className='small-txt-blk'> {opcen.category}</small>
        </div>
        <div style={{ alignItems: "center", margin: "auto" }}>
          put stats here
        </div>
      </div>

      {/* reported incidents */}
      <div
        style={{
          width: "95%",
          height: "8%",
          display: "flex",
          top: "0px",
          // alignContent: "center",
          // alignItems: "center",
          justifyContent: "space-between",
          marginTop: "10px",
          backgroundColor: "#fff",
          padding: "5px",
        }}
      >
        <div>
          <h1> CURRENT REPORTED INCIDENTS</h1>
          <div style={{ display: "flex", justifyContent: "space-between" }}>
            <h1>
              <Moment format='ll'>{date}</Moment>
            </h1>
            <h1>, </h1>
            <h1>
              <Clock
                format={"HH:mm:ss"}
                ticking={true}
                timezone={"Asia/Manila"}
              />
            </h1>
          </div>
        </div>
      </div>
    </Fragment>
  );
};

Incidents.propTypes = {
  getIncidents: PropTypes.func.isRequired,
  incident: PropTypes.object.isRequired,
  opcen: PropTypes.object.isRequired,
  opcen_id: PropTypes.object.isRequired,
  opcen_profile: PropTypes.object.isRequired,
};

const mapStateToProps = state => ({
  incident: state.incident,
  opcen: state.opcen.opcen,
  opcen_id: state.opcen.opcen._id,
  opcen_profile: state.opcen_profile,
  user: state.auth._id,
});

const mapDispatchToProps = { getIncidents };

export default connect(mapStateToProps, mapDispatchToProps)(Incidents);
