import React, { useState, useCallback } from "react";
import {
  GoogleMap,
  LoadScript,
  Marker,
  InfoWindow,
  Autocomplete,
  StandaloneSearchBox,
} from "@react-google-maps/api";
import Geocode from "react-geocode";

Geocode.setApiKey("AIzaSyAJjCD4TVrZk6u-OxFvk00OYpBqSMB_RVo");

const Map = () => {
  const [map, setMap] = useState({
    adddress: "",
    city: "",
    area: "",
    state: "",
    zoom: 15,
    height: 400,
    mapPosition: {
      // Load to specific address/location
      lat: 10.3272994,
      lng: 123.9431079,
      // lat: 0,
      // lng: 0,
    },
    markerPosition: {
      // Load to specific address/location
      // lat: 10.3272994,
      // lng: 123.9431079,
      lat: 0,
      lng: 0,
    },
  });

  const onLoad = useCallback(function callback(map) {
    // console.log("autocomplete: ", autocomplete);
    console.log("My map", map);
  }, []);

  const onUnmount = useCallback(function callback(map) {
    setMap(null);
  }, []);

  const mapContainerStyle = {
    width: "50%",
    height: "200px",
    padding: ".5rem",
    marginTop: ".5rem",
    maxWidth: 1500,
    margin: "auto",
    borderRadius: "5px",
  };
  const onPlaceChanged = place => {
    // if (this.autocomplete !== null) {
    //   console.log(this.autocomplete.getPlace());
    // } else {
    //   console.log("Autocomplete is not loaded yet!");
    // }
    const address = place.formatted_address,
      addressArray = place.address_components,
      newLat = place.geometry.location.lat(),
      newLng = place.geometry.location.lng();
  };

  return (
    <LoadScript
      id='script-loader'
      googleMapsApiKey='AIzaSyAJjCD4TVrZk6u-OxFvk00OYpBqSMB_RVo'
      libraries={["places"]}
    >
      <GoogleMap
        mapContainerStyle={mapContainerStyle}
        zoom={5}
        center={{ lat: map.mapPosition.lat, lng: map.mapPosition.lng }}
        id='map'
      >
        <StandaloneSearchBox onLoad={onLoad} onPlacesChanged={onPlaceChanged}>
          <input
            type='text'
            placeholder='Customized your placeholder'
            style={{
              boxSizing: `border-box`,
              border: `1px solid transparent`,
              width: `240px`,
              height: `32px`,
              padding: `0 12px`,
              borderRadius: `3px`,
              boxShadow: `0 2px 6px rgba(0, 0, 0, 0.3)`,
              fontSize: `14px`,
              outline: `none`,
              textOverflow: `ellipses`,
              position: "absolute",
              left: "50%",
              marginLeft: "-120px",
            }}
          />
        </StandaloneSearchBox>
      </GoogleMap>
    </LoadScript>
  );
};

export default Map;
