import React, { Fragment, useEffect, useState } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { getIncidents } from "../../actions/incident";
import { getOpcenProfileById } from "../../actions/opcenprofile";
import Clock from "react-live-clock";
import Moment from "react-moment";

import Spinner from "../layout/Spinner";

const Incidents = ({ getIncidents, incident: { loading, incidents } }) => {
  useEffect(() => {
    getIncidents();
    getOpcenProfileById();
  }, []);

  return loading ? (
    <Spinner />
  ) : (
    <Fragment>
      <div className='full'>
        <div
          style={{
            width: "90%",
            height: "8%",
            display: "flex",
            top: "0px",
            // alignContent: "center",
            // alignItems: "center",
            justifyContent: "space-between",
            marginTop: "10px",
            backgroundColor: "#fff",
            padding: "5px",
          }}
        >
          <h1>INCIDENTS REPORTED</h1>
          <div>
            <Moment format='ll'>{date}</Moment>
            <h1>
              <Clock
                format={"HH:mm:ss"}
                ticking={true}
                timezone={"Asia/Manila"}
              />
            </h1>
          </div>
        </div>
      </div>
    </Fragment>
  );
};

Incidents.propTypes = {
  getIncidents: PropTypes.func.isRequired,
  incident: PropTypes.object.isRequired,
};

const mapStateToProps = state => ({
  incident: state.incident,
});

const mapDispatchToProps = { getIncidents };

export default connect(mapStateToProps, mapDispatchToProps)(Incidents);
