import React, { Fragment, useEffect } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import Dashboard from "../operation-center/Dashboard_opcen";
import Spinner from "../layout/Spinner";
import {
  getOpcenProfileById,
  getOpcenProfiles,
} from "../../actions/opcenprofile";

export const OpcenConsole = ({
  getOpcenProfileById,
  getOpcenProfiles,
  opcenmain: { opcen, loading },
  user,
}) => {
  useEffect(() => {
    getOpcenProfileById(user);
    getOpcenProfiles();
    //Load opcen profile
  }, []);
  return (
    <Fragment>
      {loading || opcen === null ? (
        <Spinner />
      ) : (
        <div
          style={{
            display: "flex",
            flexDirection: "row",
            justifyContent: "flex-start",
            alignItems: "center",
            background: "#fff",
            borderRadius: "10px",
            marginBottom: "5px",
            height: "100px",
          }}
        >
          <div>
            <img
              style={{
                height: "88px",
                width: "88px",
                padding: "8px",
              }}
              src='/img/mandaue.png.png'
              alt=''
            />
          </div>
          <div>
            <h2> {opcen.name}</h2>
            <p> Category: {opcen.category}</p>
          </div>
        </div>
      )}
      <Dashboard />
    </Fragment>
  );
};

OpcenConsole.propTypes = {
  opcen: PropTypes.object.isRequired,
  getOpcenProfileById: PropTypes.func.isRequired,
};

const mapStateToProps = state => ({
  opcen: state.opcen.opcen,
  opcenmain: state.opcen,
  user: state.auth.user._id,
});

export default connect(mapStateToProps, {
  getOpcenProfileById,
})(OpcenConsole);
