import React, { Fragment, useState, useEffect } from "react";
import {
  InfoWindow,
  withScriptjs,
  withGoogleMap,
  GoogleMap,
  Marker,
} from "react-google-maps";
import Geodcode from "react-geocode";
import AutoComplete from "react-google-autocomplete";

Geodcode.setApiKey("AIzaSyAJjCD4TVrZk6u-OxFvk00OYpBqSMB_RVo");

const Map = () => {
  const [map, setMap] = useState({
    adddress: "",
    city: "",
    area: "",
    state: "",
    zoom: 15,
    height: 400,
    mapPosition: {
      // Load to specific address/location
      // lat: 10.3272994,
      // lng: 123.9431079,
      lat: 0,
      lng: 0,
    },
    markerPosition: {
      // Load to specific address/location
      // lat: 10.3272994,
      // lng: 123.9431079,
      lat: 0,
      lng: 0,
    },
  });

  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(position => {
        setMap(
          {
            mapPosition: {
              lat: position.coords.latitude,
              lng: position.coords.longitude,
            },
            markerPosition: {
              lat: position.coords.latitude,
              lng: position.coords.longitude,
            },
          },
          () => {
            Geodcode.fromLatLng(
              position.coords.latitude,
              position.coords.longitude
            ).then(response => {
              console.log("Response", response);

              const address = response.results[0].formatted_address,
                addressArray = response.results[0].address_components,
                city = this.getCity(addressArray),
                state = this.getState(addressArray),
                area = this.getArea(addressArray);

              console.log("city is", city);
              console.log("Area is", area);
              console.log("state is", state);
              console.log("Address is", address);

              this.setState({
                address: address ? address : "",
                city: city ? city : "",
                area: area ? area : "",
                state: state ? city : "",
              });
            });
          }
        );
      });
    }
  }, []);

  const getArea = addressArray => {
    let area = "";
    for (let index = 0; index < addressArray.length; index++) {
      if (addressArray[index].types[0]) {
        for (let j = 0; j < addressArray.length; j++) {
          if (
            "sublocality_level_1" === addressArray[index].types[j] ||
            "locality" === addressArray[index].types[j]
          ) {
            area = addressArray[index].long_name;
            return area;
          }
        }
      }
    }
  };

  const getState = addressArray => {
    let state = "";
    for (let index = 0; index < addressArray.length; index++) {
      for (let index = 0; index < addressArray.length; index++) {
        if (
          addressArray[index].types[0] &&
          "administrative_area_level_2" === addressArray[index].types[0]
        ) {
          state = addressArray[index].long_name;
          return state;
        }
      }
    }
  };

  const onMarkerDragEnd = e => {
    let newLat = e.latLng.lat();
    let newLng = e.latLng.lng();

    console.log("New Latitude", newLat);
    console.log("New Longitude", newLng);

    Geodcode.fromLatLng(newLat, newLng).then(response => {
      console.log("Response", response);

      const address = response.results[0].formatted_address,
        addressArray = response.results[0].address_components,
        city = this.getCity(addressArray),
        state = this.getState(addressArray),
        area = this.getArea(addressArray);

      console.log("city is", city);
      console.log("Area is", area);
      console.log("state is", state);
      console.log("Address is", address);

      this.setState({
        address: address ? address : "",
        city: city ? city : "",
        area: area ? area : "",
        state: state ? city : "",
        markerPosition: {
          lat: newLat,
          lng: newLng,
        },
        mapPosition: {
          lat: newLat,
          lng: newLng,
        },
      });
    });
  };

  const onPlaceSelected = place => {
      const address = place.formatted_address,
        addressArray = place.address_components,
        city = this.getCity(addressArray),
        area = this.getArea(addressArray),
        state = this.getState(addressArray),
        newLat = place.geometry.location.lat(),
        newLng = place.geometry.location.lng();

      this.setState({
        address: address ? address : "",
        city: city ? city : "",
        area: area ? area : "",
        state: state ? city : "",
        markerPosition: {
          lat: newLat,
          lng: newLng,
        },
        mapPosition: {
          lat: newLat,
          lng: newLng,
        },
      });
      console.log("Place is", place);
    },
    MapWithAMarker = withScriptjs(
      withGoogleMap(props => (
        <GoogleMap
          defaultZoom={setMap.zoom}
          defaultCenter={{
            lat: 10.3272994,
            lng: 123.9431079,
          }}
        >
          <AutoComplete
            className='autocomplete'
            types={["(regions)"]}
            onPlaceSelected={onPlaceSelected}
          />

          <Marker
            draggable={true}
            onDragEnd={onMarkerDragEnd}
            position={{
              lat: map.markerPosition.lat,
              lng: map.markerPosition.lng,
            }}
          >
            <InfoWindow>
              <div>test</div>
            </InfoWindow>
          </Marker>
          <div
            style={{
              width: "100%",
              marginTop: "5px",
            }}
          ></div>
        </GoogleMap>
      ))
    );

  return (
    <Fragment>
      <div
        style={{
          padding: ".5rem",
          marginTop: ".5rem",
          maxWidth: 1500,
        }}
      >
        <MapWithAMarker
          googleMapURL='https://maps.googleapis.com/maps/api/js?key=AIzaSyAJjCD4TVrZk6u-OxFvk00OYpBqSMB_RVo&v=3.exp&libraries=geometry,drawing,places'
          loadingElement={<div style={{ height: `50%` }} />}
          containerElement={<div className='map' />}
          mapElement={<div style={{ height: `100%` }} />}
        />
      </div>

      <div style={{ padding: "1rem", marginTop: "1rem" }}>
        <div>{setMap.address}</div>
      </div>
    </Fragment>
  );
};

export default Map;
