import React, { useState, useCallback } from "react";
import {
  GoogleMap,
  useJsApiLoader,
  Marker,
  InfoWindow,
  Autocomplete,
} from "@react-google-maps/api";
import Geocode from "react-geocode";

Geocode.setApiKey("AIzaSyAJjCD4TVrZk6u-OxFvk00OYpBqSMB_RVo");

const Map = () => {
  const { isLoaded } = useJsApiLoader({
    id: "google-map-script",
    googleMapsApiKey: "AIzaSyAJjCD4TVrZk6u-OxFvk00OYpBqSMB_RVo",
  });

  const [map, setMap] = useState({
    adddress: "",
    city: "",
    area: "",
    state: "",
    zoom: 15,
    height: 400,
    mapPosition: {
      // Load to specific address/location
      lat: 10.3272994,
      lng: 123.9431079,
      // lat: 0,
      // lng: 0,
    },
    markerPosition: {
      // Load to specific address/location
      // lat: 10.3272994,
      // lng: 123.9431079,
      lat: 0,
      lng: 0,
    },
  });

  const onLoad = useCallback(function callback(map) {
    console.log("My map", map);
  }, []);

  const onUnmount = useCallback(function callback(map) {
    setMap(null);
  }, []);

  const containerStyle = {
    width: "50%",
    height: "200px",
    padding: ".5rem",
    marginTop: ".5rem",
    maxWidth: 1500,
    margin: "auto",
    borderRadius: "5px",
  };
  const onPlaceSelected = place => {
    const address = place.formatted_address,
      addressArray = place.address_components,
      // city = this.getCity(addressArray),
      // area = this.getArea(addressArray),
      // state = this.getState(addressArray),
      newLat = place.geometry.location.lat(),
      newLng = place.geometry.location.lng();

    setMap({
      address: address ? address : "",
      // city: city ? city : "",
      // area: area ? area : "",
      // state: state ? city : "",
      markerPosition: {
        lat: newLat,
        lng: newLng,
      },
      mapPosition: {
        lat: newLat,
        lng: newLng,
      },
    });
    console.log("Place is", place);
  };

  return isLoaded ? (
    <GoogleMap
      mapContainerStyle={containerStyle}
      center={map.mapPosition}
      zoom={map.zoom}
      onLoad={onLoad}
      onUnmount={onUnmount}

      // defaultOptions={{ styles: mapStyles }}
    >
      {/* <Autocomplete onLoad={onLoad} onPlaceChanged={onPlaceSelected}>
        <input
          type='text'
          placeholder='Customized your placeholder'
          style={{
            boxSizing: `border-box`,
            border: `1px solid transparent`,
            width: `240px`,
            height: `32px`,
            padding: `0 12px`,
            borderRadius: `3px`,
            boxShadow: `0 2px 6px rgba(0, 0, 0, 0.3)`,
            fontSize: `14px`,
            outline: `none`,
            textOverflow: `ellipses`,
            position: "absolute",
            left: "50%",
            marginLeft: "-120px",
          }}
        />
      </Autocomplete> */}

      <Marker position={map.mapPosition}></Marker>
    </GoogleMap>
  ) : (
    <> </>
  );
};

export default Map;
