import React, { Fragment, useState } from "react";
import { Link, withRouter } from "react-router-dom";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { addEmergencyInfo } from "../../actions/profile";

const AddEmergencyInfo = ({ addEmergencyInfo, history }) => {
  const [formData, setFormData] = useState({
    contactperson: "",
    relationship: "",
    contactnumber: "",
    address: "",
    bloodtype: "",
    build: "",
    birthmark: "",
    height: "",
    weight: "",
    insured: "",
  });

  const {
    contactperson,
    relationship,
    contactnumber,
    address,
    bloodtype,
    build,
    birthmark,
    height,
    weight,
    insured,
  } = formData;

  const onChange = async c =>
    setFormData({ ...formData, [c.target.name]: c.target.value });

  const onSubmit = async c => {
    c.preventDefault();
    addEmergencyInfo(formData, history);
  };

  return (
    <Fragment>
      <h1 className='large text-primary'>Emergency information</h1>
      <p className='lead'>
        <i className='fas fas fa-first-aid'></i> Add details incase of emergency
      </p>
      <small>* = required field</small>
      <form className='form' onSubmit={c => onSubmit(c)}>
        <div className='form-group'>
          <input
            type='text'
            placeholder='* Contact Person'
            name='contactperson'
            value={contactperson}
            onChange={c => onChange(c)}
            required
          />
        </div>

        <div className='form-group'>
          <input
            type='text'
            placeholder='* Relationship'
            name='relationship'
            value={relationship}
            onChange={c => onChange(c)}
            required
          />
        </div>
        <div className='form-group'>
          <input
            type='tel'
            placeholder='* 09XX XXX XXXX'
            name='contactnumber'
            value={contactnumber}
            onChange={c => onChange(c)}
            required
          />
        </div>
        <div className='form-group'>
          <input
            type='text'
            placeholder='* Address'
            name='address'
            value={address}
            onChange={c => onChange(c)}
            required
          />
        </div>

        <div className='form-group'>
          <input
            type='text'
            placeholder='Blood Type'
            name='bloodtype'
            value={bloodtype}
            onChange={c => onChange(c)}
          />
        </div>
        <div className='form-group'>
          <input
            type='text'
            placeholder='Body Build'
            name='build'
            value={build}
            onChange={c => onChange(c)}
          />
        </div>
        <div className='form-group'>
          <input
            type='text'
            placeholder='Birth Mark'
            name='birthmark'
            value={birthmark}
            onChange={c => onChange(c)}
          />
        </div>

        <div className='form-group'>
          <input
            type='text'
            placeholder='Height'
            name='height'
            value={height}
            onChange={c => onChange(c)}
          />
        </div>
        <div className='form-group'>
          <input
            type='text'
            placeholder='Weight'
            name='weight'
            value={weight}
            onChange={c => onChange(c)}
          />
        </div>

        <div className='form-group'>
          <select name='insured' value={insured} onChange={c => onChange(c)}>
            <option value='0'>Insured</option>
            <option value='Yes'>Yes</option>
            <option value='No'>No</option>
          </select>
        </div>

        <input type='submit' className='btn btn-primary my-1' />
        <Link className='btn btn-light my-1' to='/dashboard'>
          Go Back
        </Link>
      </form>
    </Fragment>
  );
};

AddEmergencyInfo.propTypes = {
  addEmergencyInfo: PropTypes.func.isRequired,
};

export default connect(null, { addEmergencyInfo })(
  withRouter(AddEmergencyInfo)
);
