import { React, useEffect, Fragment } from "react";
import PropTypes from "prop-types";
import { getCurrentProfile } from "../../actions/profile";
import { connect } from "react-redux";
import Spinner from "../layout/Spinner";
import { Link } from "react-router-dom";
import profile from "../../reducers/profile";
import { QRCode } from "react-qrcode-logo";

const Id = ({ getCurrentProfile, profile: { profile, loading }, auth }) => {
  useEffect(() => {
    getCurrentProfile();
    console.log(profile);
  }, [getCurrentProfile]);

  return (
    <Fragment>
      {profile === null || loading ? (
        <Spinner />
      ) : (
        <Fragment>
          <div
            style={{
              display: "flex",
              flexDirection: "column",
              alignItems: "center",
              height: "100vh",
            }}
          >
            <div
              style={{
                width: "400px",
                objectFit: "contain",
                margin: "auto",
                height: "35vh",
              }}
            >
              <img
                style={{
                  display: "flex",
                  width: "80%",
                  margin: "auto",
                }}
                src={`/img/${profile.profilepic}`}
                alt=''
              />
            </div>
            <h2
              style={{
                display: "flex",
                flexDirection: "column",
                height: "45px",
                width: "90%",
                alignItems: "center",
                // padding: "20px",
                // backgroundColor: "#ddd",
                // borderRadius: "10px  10px 0px 0px",
                marginTop: "6px",
                borderBottom: "2px solid #ccc",
              }}
            >
              {profile.user.lname} , {profile.user.name}
            </h2>
            <div
              style={{
                display: "flex",
                flexDirection: "column",
                height: "80px",
                width: "80%",
                // padding: "20px",
                // backgroundColor: "#ddd",
                // borderRadius: "10px  10px 0px 0px",
                marginTop: "6px",
                alignItems: "center",
              }}
            >
              <h1>{profile.status}</h1>
              <p>Position</p>
            </div>
            <div>{profile.completeaddress}</div>
            <div> {profile.area}</div>
            <h3> {profile.organization}</h3>
          </div>
          <div className='qr-code'>
            <QRCode
              size='160'
              includeMargin='true'
              fgColor='#000000'
              value={profile.user._id}
            />
          </div>
        </Fragment>
      )}
    </Fragment>
  );
};

Id.propTypes = {
  getCurrentProfile: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
  profile: PropTypes.object.isRequired,
  auth: PropTypes.object.isRequired,
};

const mapStateToProps = state => ({
  profile: state.profile,
  auth: state.auth,
});
export default connect(mapStateToProps, { getCurrentProfile })(Id);
