import React, { Fragment, useState, useRef, useCallback } from "react";
import { Redirect } from "react-router-dom";
import { connect } from "react-redux";
import { setAlert } from "../../actions/alert";
import { registerOpcen } from "../../actions/opcen";
import PropTypes from "prop-types";
import Spinner from "../layout/Spinner";

import {
  GoogleMap,
  useLoadScript,
  Marker,
  InfoWindow,
} from "@react-google-maps/api";
import MapStyles from "../layout/MapStyles";
import Moment from "react-moment";
import Geodcode from "react-geocode";
import Search from "../../utils/searchMap";
import Locate from "../../utils/locateMap";

const libraries = ["places"];

const mapContainerStyle = {
  height: `250px`,
  width: "100%",
  borderRadius: "5px",
};

const center = {
  lat: 10.336425,
  lng: 123.90789,
};
const options = {
  styles: MapStyles,
  disableDefaultUI: true,
  zoomControl: true,
};
const IncidentDetails = ({ isAuthenticated }) => {
  const [formData, setFormData] = useState({
    type: "",
    completeaddress: "",
    lat: "",
    lng: "",
    city: "",
    state: "",
    area: "",
  });

  const [marker, setMarker] = useState({ lat: 10.336425, lng: 123.90789 });
  const [com_address, setAddress] = useState({
    currentaddress: "",
    city: "",
    area: "",
    state: "",
  });

  const { isLoaded, loadError } = useLoadScript({
    googleMapsApiKey: process.env.REACT_APP_GOOGLE_MAPS_API_KEY,
    libraries,
  });

  Geodcode.setApiKey(process.env.REACT_APP_GOOGLE_MAPS_API_KEY);

  const [selected, setSelected] = useState(null);

  const getCity = addressArray => {
    let city = "";
    for (let index = 0; index < addressArray.length; index++) {
      if (
        addressArray[index].types[0] &&
        "administrative_area_level_2" === addressArray[index].types[0]
      ) {
        city = addressArray[index].long_name;
        return city;
      }
    }
  };

  const getArea = addressArray => {
    let area = "";
    for (let index = 0; index < addressArray.length; index++) {
      if (addressArray[index].types[0]) {
        for (let j = 0; j < addressArray.length; j++) {
          if (
            "sublocality_level_1" === addressArray[index].types[j] ||
            "locality" === addressArray[index].types[j]
          ) {
            area = addressArray[index].long_name;
            return area;
          }
        }
      }
    }
  };

  const getState = addressArray => {
    let state = "";
    for (let index = 0; index < addressArray.length; index++) {
      for (let index = 0; index < addressArray.length; index++) {
        if (
          addressArray[index].types[0] &&
          "administrative_area_level_2" === addressArray[index].types[0]
        ) {
          state = addressArray[index].long_name;
          return state;
        }
      }
    }
  };

  const onMapClick = useCallback(e => {
    const latlng = { lat: e.latLng.lat(), lng: e.latLng.lng() };
    setMarker(latlng);
    console.log("create profile onmapclick", marker);
    panTo(latlng);

    Geodcode.fromLatLng(e.latLng.lat(), e.latLng.lng()).then(response => {
      console.log("Response", response);
      const address = response.results[0].formatted_address,
        addressArray = response.results[0].address_components,
        city = getCity(addressArray),
        state = getState(addressArray),
        area = getArea(addressArray);

      setAddress({
        currentaddress: address ? address : "",
        city: city ? city : "",
        area: area ? area : "",
        state: state ? city : "",
      });
    });
  }, []);
  const dropRef = useRef(); // React ref for managing the hover state of droppable area
  const mapRef = useRef();

  const onMapLoad = useCallback(map => {
    mapRef.current = map;
    console.log("MapRef load is ", map);
    Geodcode.fromLatLng(map.center.lat(), map.center.lng()).then(response => {
      console.log("Response", response);
      const address = response.results[0].formatted_address,
        addressArray = response.results[0].address_components,
        city = getCity(addressArray),
        state = getState(addressArray),
        area = getArea(addressArray);

      setAddress({
        currentaddress: address ? address : "",
        city: city ? city : "",
        area: area ? area : "",
        state: state ? city : "",
      });
      setMarker({ lat: map.center.lat(), lng: map.center.lng() });
    });
  }, []);

  const panTo = useCallback(({ lat, lng }) => {
    setMarker({ lat, lng });
    mapRef.current.panTo({ lat, lng });
    mapRef.current.setZoom(15);
  }, []);

  if (loadError) return "Error Loading Map";
  if (!isLoaded) return "Loading Map...";
  // if (com_address)

  const { type, completeaddress, lat, lng, city, state, area } = formData;

  const onChange = async c =>
    setFormData({ ...formData, [c.target.type]: c.target.value });

  const onSubmit = async c => {
    c.preventDefault();
    registerOpcen({
      type,
      completeaddress,
      lat,
      lng,
      city,
      state,
      area,
    });
  };
  if (isAuthenticated) {
    // return <Redirect to={`/operation-center/${user._id}/${opcen._id}`} />;
  }
  //   return <Redirect to={`/operation-center`} />;
  // }

  return (
    <Fragment>
      <div
        style={{
          padding: "20px",
          background: "#fff",
          borderRadius: "10px",
          margin: "1rem",
        }}
      >
        <h1 className='large text-primary'>Incident Details</h1>
        <p className='lead'>
          <i className='fa fa-building-o'></i> Operation Center Information
        </p>
        <form className='form' onSubmit={c => onSubmit(c)}>
          <Fragment>
            {/* google map redered here */}
            <div style={{ display: "block", flexDirection: "row" }}>
              {/* <Locate panTo={panTo} /> */}
              <GoogleMap
                mapContainerStyle={mapContainerStyle}
                zoom={13}
                center={center}
                options={options}
                onClick={onMapClick}
                onLoad={onMapLoad}
              >
                <div
                  style={{
                    display: "flex",
                    position: "relative",
                    alignContent: "center",
                    width: "100%",
                    zIndex: "1",
                    margin: "1px 2px 2px 1px",
                    marginTop: "2px",
                  }}
                >
                  <Search panTo={panTo} />
                  <Locate panTo={panTo} />
                </div>
                <div
                  style={{
                    display: "flex",
                    position: "relative",

                    width: "100%",
                    zIndex: "1",
                    margin: "1px 2px 2px 1px",
                    marginTop: "2px",
                  }}
                ></div>
                <Marker
                  position={{
                    lat: marker.lat,
                    lng: marker.lng,
                  }}
                  icon={{
                    url: "/icons/map/pin.png",
                    scaledSize: new window.google.maps.Size(30, 30),
                    origin: new window.google.maps.Point(0, 0),
                    anchor: new window.google.maps.Point(15, 15),
                  }}
                  onClick={() => {
                    setSelected(marker);
                  }}
                />

                {selected ? (
                  <InfoWindow
                    position={{ lat: selected.lat, lng: selected.lng }}
                    onCloseClick={() => {
                      setSelected(null);
                    }}
                  >
                    <div
                      style={{
                        display: "block",
                        alignContent: "center",
                        justifyContent: "center",
                      }}
                    >
                      <div>
                        <h4>Close</h4>
                      </div>

                      <div>
                        <p> Incident!</p>
                      </div>
                      <p>
                        Date{" "}
                        <Moment fromNow ago='LLLL'>
                          {selected.time}
                        </Moment>
                      </p>
                    </div>
                  </InfoWindow>
                ) : null}
              </GoogleMap>

              <div className='form-group'>
                <input
                  type='text'
                  name='completeaddress'
                  value={completeaddress}
                  placeholder={com_address.currentaddress}
                  onChange={c => onChange(c)}
                />
                <small className='form-text'> Current location</small>
                <input
                  style={{ display: "none" }}
                  type='text'
                  name='city'
                  value={city}
                  placeholder={city}
                  onChange={c => onChange(c)}
                />
                <small className='form-text' style={{ display: "none" }}>
                  area
                </small>
                <input
                  style={{ display: "none" }}
                  type='text'
                  name='area'
                  value={area}
                  placeholder={com_address.area}
                  onChange={c => onChange(c)}
                />
                <small className='form-text' style={{ display: "none" }}>
                  Your area
                </small>
                <input
                  style={{ display: "none" }}
                  type='text'
                  name='lat'
                  value={lat}
                  placeholder={marker.lat}
                  onChange={c => onChange(c)}
                />
                <small className='form-text' style={{ display: "none" }}>
                  Your latitude
                </small>
                <input
                  style={{ display: "none" }}
                  type='text'
                  name='lng'
                  value={lng}
                  placeholder={marker.lng}
                  onChange={c => onChange(c)}
                />
              </div>
            </div>
          </Fragment>
          <input className='btn-full alert-danger' type='submit' value='Send' />
        </form>
      </div>
    </Fragment>
  );
};

