import { React, useEffect, Fragment } from "react";
import PropTypes from "prop-types";
import { getCurrentProfile } from "../../actions/profile";
import { connect } from "react-redux";
import Spinner from "../layout/Spinner";
import { Link } from "react-router-dom";
import profile from "../../reducers/profile";
import { QRCode } from "react-qrcode-logo";

const Id = ({ getCurrentProfile, profile: { profile, loading }, auth }) => {
  useEffect(() => {
    getCurrentProfile();
    console.log(profile);
  }, [getCurrentProfile]);

  return (
    <Fragment>
      {profile === null || loading ? (
        <Spinner />
      ) : (
        <Fragment>
          <div className='id-container'>
            <div style={{ backgroundColor: "rgba(0, 0, 0, 0.7)" }}>
              <div
                style={{
                  background: "#666",
                  width: "60px",
                  height: "10px",
                  borderRadius: "5px",
                }}
              ></div>
              <div
                style={{
                  width: "99%",
                  border: "1px #ddd solid",
                  borderRadius: "5px",
                  marginTop: "10px",
                  height: "100%",
                }}
              >
                <div
                  style={{
                    display: "flex",
                    flexDirection: "row",
                    // padding: "10px",
                    height: "40%",
                    borderRadius: " 5px 5px 0px 0px",
                    backgroundColor: "#AFBFC6",
                  }}
                >
                  <div style={{ margin: "auto", padding: "2px" }}>
                    <img
                      className='profile-img'
                      src={`/img/${profile.profilepic}`}
                      alt=''
                    />
                  </div>
                  <div className='qr-code-id'>
                    <QRCode
                      size='125'
                      includeMargin='true'
                      fgColor='#333'
                      value={profile.user._id}
                    />
                  </div>
                </div>
                <p
                  style={{
                    width: "100%",
                    backgroundColor: "#dc3545",
                    color: "white",
                  }}
                >
                  Valid Until: Dec. 31, 2021
                </p>
                <div
                  style={{
                    background: "#215a75",
                    width: "100%",
                    height: "10%",
                    textAlign: "center",
                    color: "white",
                    padding: "8px",
                  }}
                >
                  <h2>
                    {profile.user.lname} , {profile.user.name}
                  </h2>
                </div>
                <p> Complete Name </p>
                <div
                  style={{
                    background: "green",
                    width: "100%",
                    textAlign: "center",
                    color: "white",
                    height: "10%",
                    padding: "15px",
                  }}
                >
                  <h5>Authorized Persons Outside Residence</h5>
                </div>
                <div
                  style={{
                    display: "flex",
                    flexDirection: "column",
                    alignItems: "center",
                    lineHeight: "20px",
                    marginTop: "10px",
                    height: "12%",
                  }}
                >
                  <h1>{profile.status}</h1>
                  <p className='post-date p-11'>at</p>
                  <h3> {profile.organization}</h3>
                </div>

                <div
                  style={{
                    display: "flex",
                    flexDirection: "row",
                    height: "18%",
                    borderRadius: " 0px 0px  5px 5px",
                  }}
                >
                  <div
                    style={{
                      display: "flex",
                      flexDirection: "column",
                      alignItems: "center",
                      width: "50%",
                      height: "100%",
                      margin: "auto",
                      backgroundColor: "blue",
                      padding: "20px",
                      borderRadius: "0 0 0 5px",
                      color: "#fff",
                    }}
                  >
                    <h4> {profile.area} </h4>
                    <p> Operation Center</p>
                  </div>
                  <div
                    style={{
                      display: "flex",
                      margin: "auto",
                      width: "50%",
                    }}
                  >
                    <img
                      className='post-profile'
                      src={`/img/${profile.profilepic}`}
                      alt=''
                    />
                  </div>
                </div>
              </div>
            </div>
          </div>
        </Fragment>
      )}
    </Fragment>
  );
};

Id.propTypes = {
  getCurrentProfile: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
  profile: PropTypes.object.isRequired,
  auth: PropTypes.object.isRequired,
};

const mapStateToProps = state => ({
  profile: state.profile,
  auth: state.auth,
});
export default connect(mapStateToProps, { getCurrentProfile })(Id);
