import React, { useState, useCallback } from "react";
import {
  GoogleMap,
  useJsApiLoader,
  Marker,
  InfoWindow,
  Autocomplete,
} from "@react-google-maps/api";
import Geocode from "react-geocode";

Geocode.setApiKey("AIzaSyAJjCD4TVrZk6u-OxFvk00OYpBqSMB_RVo");

const Map = () => {
  const { isLoaded } = useJsApiLoader({
    id: "google-map-script",
    googleMapsApiKey: "AIzaSyAJjCD4TVrZk6u-OxFvk00OYpBqSMB_RVo",
  });

  const [map, setMap] = useState({
    adddress: "",
    city: "",
    area: "",
    state: "",
    zoom: 15,
    height: 400,
    mapPosition: {
      // Load to specific address/location
      lat: 10.3272994,
      lng: 123.9431079,
      // lat: 0,
      // lng: 0,
    },
    markerPosition: {
      // Load to specific address/location
      // lat: 10.3272994,
      // lng: 123.9431079,
      lat: 0,
      lng: 0,
    },
  });

  const onLoad = useCallback(function callback(map) {
    console.log("My map", map);
  }, []);

  const onUnmount = useCallback(function callback(map) {
    setMap(null);
  }, []);

  const containerStyle = {
    width: "50%",
    height: "200px",
    padding: ".5rem",
    marginTop: ".5rem",
    maxWidth: 1500,
    margin: "auto",
    borderRadius: "5px",
  };

  return isLoaded ? (
    <GoogleMap
      mapContainerStyle={containerStyle}
      center={map.mapPosition}
      zoom={map.zoom}
      onLoad={onLoad}
      onUnmount={onUnmount}

      // defaultOptions={{ styles: mapStyles }}
    >
      {/* <AutoComplete
          className='autocomplete'
          types={["(regions)"]}
           onPlaceSelected={onPlaceSelected}
        /> */}

      <Marker position={map.mapPosition}></Marker>
    </GoogleMap>
  ) : (
    <> </>
  );
};

export default Map;
