import { React, useEffect, Fragment } from "react";
import PropTypes from "prop-types";
import { getCurrentProfile } from "../../actions/profile";
import { connect } from "react-redux";
import Spinner from "../layout/Spinner";
import { Link } from "react-router-dom";
import profile from "../../reducers/profile";
import { QRCode } from "react-qrcode-logo";

const Id = ({ getCurrentProfile, profile: { profile, loading }, auth }) => {
  useEffect(() => {
    getCurrentProfile();
    console.log(profile);
  }, [getCurrentProfile]);

  return (
    <Fragment>
      {profile === null || loading ? (
        <Spinner />
      ) : (
        <Fragment>
          <div className='id-container'>
            <div>
              <div
                style={{
                  display: "flex",
                  flexDirection: "row",
                  padding: "20px",
                }}
              >
                <div style={{ margin: "auto", padding: "10px" }}>
                  <img
                    className='profile-img'
                    src={`/img/${profile.profilepic}`}
                    alt=''
                  />
                </div>
                <div className='qr-code-id'>
                  <QRCode
                    size='125'
                    includeMargin='true'
                    fgColor='#333'
                    value={profile.user._id}
                  />
                </div>
              </div>

              <div
                style={{
                  background: "#215a75",
                  width: "100%",
                  textAlign: "center",
                  color: "white",
                }}
              >
                <h2>
                  {profile.user.lname} , {profile.user.name}
                </h2>
              </div>
              <div
                style={{
                  display: "flex",
                  flexDirection: "column",
                  alignItems: "center",
                }}
              >
                <h1>{profile.status}</h1>
                <p className='post-date p-11'>Position</p>
                <h3> {profile.organization}</h3>
              </div>
              <div>{profile.completeaddress}</div>
              <div> {profile.area}</div>
            </div>
          </div>
        </Fragment>
      )}
    </Fragment>
  );
};

Id.propTypes = {
  getCurrentProfile: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
  profile: PropTypes.object.isRequired,
  auth: PropTypes.object.isRequired,
};

const mapStateToProps = state => ({
  profile: state.profile,
  auth: state.auth,
});
export default connect(mapStateToProps, { getCurrentProfile })(Id);
