import React, { Fragment, useEffect } from "react";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import { getOpcen } from "../../actions/opcen";
import Spinner from "../layout/Spinner";
import { Link } from "react-router-dom";

export const OpcenItem = ({
  getOpcen,
  opcen: { _id, user, name, category, type, date, loading },
}) => {
  return (
    <Fragment>
      {" "}
      {loading ? (
        <Spinner />
      ) : (
        <Fragment>
          <div
            style={{
              display: "flex",
              flexDirection: "row",
              width: "200px",
              height: "200px",
              justifyContent: "space-around",
            }}
          >
            <div
              style={{
                padding: "5px",
                background: "#fff",
                borderRadius: "10px",
                width: "200px",
                height: "200px",
              }}
            >
              <div
                style={{
                  display: "flex",
                  flexDirection: "row",
                  width: "100%",
                  height: "100%",
                  margin: "auto",
                  justifyContent: "space-around",
                }}
              >
                <Link
                  className='bigIcon'
                  onClick={c => getOpcen(user, _id)}
                  to={`/operation-center/${user}/${_id}`}
                >
                  <div
                    style={{
                      height: "40px",
                      margin: "auto",
                      padding: "8px",
                      height: "40px",
                      width: "40px",
                      borderRadius: "50%",
                      background: "#ccc",
                    }}
                  >
                    {type === "Gov" ? (
                      <i className='fas fa-landmark' aria-hidden='true'></i>
                    ) : type === "Pri" ? (
                      <i className='fa fa-hand-paper-o' aria-hidden='true'></i>
                    ) : (
                      <i className='fa fa-building-o' aria-hidden='true'></i>
                    )}
                  </div>

                  <p style={{ fontSize: "14px", lineHeight: "10px" }}>{name}</p>

                  <p style={{ fontSize: "12px", lineHeight: "10px" }}>
                    {category}
                  </p>
                  <p style={{ fontSize: "12px", lineHeight: "10px" }}>
                    {" "}
                    {type}{" "}
                  </p>
                </Link>
              </div>
            </div>
          </div>
        </Fragment>
      )}
    </Fragment>
  );
};

OpcenItem.propTypes = {
  opcen: PropTypes.object.isRequired,
};
const mapStateToProps = state => ({
  auth: state.auth,
});

export default connect(mapStateToProps, { getOpcen })(OpcenItem);
