import React, { useState } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { addPost } from "../../actions/post";

const PostForm = ({ addPost }) => {
  //const [text, setText] = useState("");
  const [formData, setFormData] = useState({
    text: " ",
  });
  const [image, setImage] = useState("");
  const [imageName, setImageName] = useState("Choose file");

  const { text } = formData;
  const onFileChange = c => {
    setImage(c.target.files[0]);
    setImageName(c.target.files[0].name);
  };

  const onChange = c => {
    setFormData({ ...formData, [c.target.name]: c.taget.value });
  };

  const onSubmit = c => {
    c.preventDefault();
  };

  const payload = new FormData();
  payload.append("text", formData.text);
  payload.append("articleImage", image);

  // addPost(payload);

  return (
    <div className='post-form'>
      <div className='bg-primary p'>
        <h3>Say Something...</h3>
      </div>
      <form
        className='form my-1'
        encType='multipart/form-data'
        onSubmit={c => onSubmit(c)}
      >
        <textarea
          name='text'
          cols='30'
          rows='5'
          placeholder='Create a post'
          value={text}
          onChange={c => onChange(c)}
          required
        ></textarea>

        {/* add image button here */}
        <div>
          <input type='submit' className='btn btn-dark my-1' value='Submit' />
          <label htmlFor='file'>
            Upload Photo
            <input
              type='file'
              onChange={c => onFileChange(c)}
              accept='image/*'
              multiple
            />
          </label>
        </div>
      </form>
    </div>
  );
};

PostForm.propTypes = {
  addPost: PropTypes.func.isRequired,
};

export default connect(null, { addPost })(PostForm);
