import {
  CREATE_OPCEN,
  ERROR_CREATE_OPCEN,
  OPCEN_REGISTER_SUCCESS,
  OPCEN_REGISTER_FAIL,
} from "../actions/types";

const initialState = {
  createOpcen: false,
  type: null,
  opcendetails: null,
  loading: true,
  error: {},
};

export default function (state = initialState, action) {
  const { type, payload } = action;

  switch (type) {
    case CREATE_OPCEN:
      return {
        ...state,
        createOpcen: true,
        type: payload, //from the action file
        loading: false,
      };
    case OPCEN_REGISTER_SUCCESS:
      return {
        ...state,
        createOpcen: true,
        type: payload, //from the action file
        loading: false,
      };

    default:
      return state;
  }
}
