import { React, useEffect, Fragment, useState } from "react";
import PropTypes from "prop-types";
import { getCurrentProfile } from "../../actions/profile";
import { connect } from "react-redux";
import Spinner from "../layout/Spinner";
import { Link } from "react-router-dom";
import profile from "../../reducers/profile";
import { QRCode } from "react-qrcode-logo";

const Id = ({ getCurrentProfile, profile: { profile, loading }, auth }) => {
  useEffect(() => {
    getCurrentProfile();
    console.log(profile);
  }, [getCurrentProfile]);

  const [displayQr, toggleDisplayQr] = useState(false);
  const [displayPhoto, toggleDisplayPhoto] = useState(true);

  return (
    <Fragment>
      {profile === null || loading ? (
        <Spinner />
      ) : (
        <Fragment>
          <div className='id-container'>
            <div
              style={{
                background: "#666",
                width: "60px",
                height: "10px",
                borderRadius: "5px",
              }}
            ></div>
            <div
              style={{
                width: "99%",
                border: "1px #ddd solid",
                borderRadius: "5px",
                marginTop: "10px",
                height: "100%",
              }}
            >
              <div
                style={{
                  display: "flex",
                  flexDirection: "column",
                  // padding: "10px",
                  height: "32%",
                  borderRadius: " 5px 5px 0px 0px",
                  // backgroundColor: "#AFBFC6",
                  backgroundColor: "#fff",
                  alignItems: "center",
                }}
              >
                {displayQr && (
                  <Fragment>
                    <div className='qr-code-id'>
                      <QRCode
                        size='125'
                        includeMargin='true'
                        fgColor='#333'
                        value={profile.user._id}
                      />
                    </div>
                    <div className='id-container-name'>
                      <h2>
                        {profile.user.lname} , {profile.user.name}
                      </h2>
                    </div>
                    <div className='id-container-access'>
                      <h5>Authorized Persons Outside Residence</h5>
                    </div>
                  </Fragment>
                )}

                {displayPhoto && (
                  <Fragment>
                    <div style={{ margin: "auto" }}>
                      <img
                        className='profile-img'
                        src={`/img/${profile.profilepic}`}
                        alt=''
                        onClick={() => toggleDisplayQr(!displayQr)}
                      />
                    </div>
                  </Fragment>
                )}
              </div>
            </div>
          </div>
        </Fragment>
      )}
    </Fragment>
  );
};

Id.propTypes = {
  getCurrentProfile: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
  profile: PropTypes.object.isRequired,
  auth: PropTypes.object.isRequired,
};

const mapStateToProps = state => ({
  profile: state.profile,
  auth: state.auth,
});
export default connect(mapStateToProps, { getCurrentProfile })(Id);
