import React, { Fragment, useEffect } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import Spinner from "../layout/Spinner";
import { getimagePost } from "../../actions/post";
import PostItem from "../posts/PostItem";

const ImagePost = ({
  getimagePost,
  post: { post, artigleImage, name, loading },
  match,
}) => {
  useEffect(() => {
    getimagePost(match.params.id);
  }, [getimagePost, match.params.id]);

  return (
    <Fragment>
      <div className='posts'>
        <img className='post-profile' src={`/img/${articleImage}`} alt='...' />
      </div>
      <h1 className='large'>{name}</h1>
    </Fragment>
  );
};

ImagePost.propTypes = {
  getimagePost: PropTypes.func.isRequired,
  post: PropTypes.object.isRequired,
};

const mapStateToProps = state => ({
  post: state.post,
});

export default connect(mapStateToProps, { getimagePost })(ImagePost);
