import React, { Fragment, useEffect, useState } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { getIncidents } from "../../actions/incident";
import { getOpcenProfileById } from "../../actions/opcenprofile";
import Clock from "react-live-clock";

import Spinner from "../layout/Spinner";

const Incidents = ({ getIncidents, incident: { loading, incidents } }) => {
  useEffect(() => {
    getIncidents();
    getOpcenProfileById();
  }, []);

  return loading ? (
    <Spinner />
  ) : (
    <Fragment>
      <div className='modal'>
        <div
          style={{
            width: "100%",
            display: "flex",

            alignContent: "center",
            alignItems: "center",
            justifyContent: "space-evenly",
          }}
        >
          <h1>CURRENT INCIDENTS</h1>
          <h1>
            <Clock
              format={"HH:mm:ss"}
              ticking={true}
              timezone={"Asia/Manila"}
            />
          </h1>
        </div>
      </div>
    </Fragment>
  );
};

Incidents.propTypes = {
  getIncidents: PropTypes.func.isRequired,
  incident: PropTypes.object.isRequired,
};

const mapStateToProps = state => ({
  incident: state.incident,
});

const mapDispatchToProps = { getIncidents };

export default connect(mapStateToProps, mapDispatchToProps)(Incidents);
